jQuery(document).ready(function($) {

	/***** Colour picker *****/

	$('.colorpicker').hide();
	$('.colorpicker').each( function() {
		$(this).farbtastic( $(this).closest('.color-picker').find('.color') );
	});

	$('.color').click(function() {
		$(this).closest('.color-picker').find('.colorpicker').fadeIn();
	});

	$(document).mousedown(function() {
		$('.colorpicker').each(function() {
			var display = $(this).css('display');
			if ( display == 'block' )
				$(this).fadeOut();
		});
	});


	/***** Uploading images *****/

	var file_frame;

	jQuery.fn.uploadMediaFile = function( button, preview_media ) {
		var button_id = button.attr('id');
		var field_id = button_id.replace( '_button', '' );
		var preview_id = button_id.replace( '_button', '_preview' );

		// If the media frame already exists, reopen it.
		if ( file_frame ) {
		  file_frame.open();
		  return;
		}

		// Create the media frame.
		file_frame = wp.media.frames.file_frame = wp.media({
		  title: jQuery( this ).data( 'uploader_title' ),
		  button: {
			text: jQuery( this ).data( 'uploader_button_text' ),
		  },
		  multiple: false
		});

		// When an image is selected, run a callback.
		file_frame.on( 'select', function() {
		  attachment = file_frame.state().get('selection').first().toJSON();
		  jQuery("#"+field_id).val(attachment.id);
		  if( preview_media ) {
			jQuery("#"+preview_id).attr('src',attachment.sizes.thumbnail.url);
		  }
		  file_frame = false;
		});

		// Finally, open the modal
		file_frame.open();
	}

	jQuery('.image_upload_button').click(function() {
		jQuery.fn.uploadMediaFile( jQuery(this), true );
	});

	jQuery('.image_delete_button').click(function() {
		jQuery(this).closest('td').find( '.image_data_field' ).val( '' );
		jQuery(this).closest('td').find( '.image_preview' ).remove();
		return false;
	});

	jQuery('#pe-main-select').on('change', function() {
		switch( jQuery(this).val() ) {
			case 'empty':
				jQuery('#pe-name-section, #pe-label-section, #pe-placeholder-section, #pe-value-section, #pe-description-section').hide();
				jQuery('#pe-empty-section, #pe-syntax-section, #pe-min-section, #pe-max-section, #pe-max-size-section, #pe-extensions-section, #pe-registration-section').hide();
				break;
			case 'text':
				jQuery('#pe-name-section, #pe-label-section, #pe-placeholder-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-empty-section, #pe-syntax-section, #pe-min-section, #pe-max-section, #pe-registration-section').show();
				jQuery('#pe-max-size-section, #pe-extensions-section').hide();
				break;
			case 'textarea':
				jQuery('#pe-name-section, #pe-label-section, #pe-placeholder-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-empty-section, #pe-syntax-section, #pe-min-section, #pe-max-section, #pe-registration-section').show();
				jQuery('#pe-max-size-section, #pe-extensions-section').hide();
				break;
			case 'checkbox':
				jQuery('#pe-name-section, #pe-label-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-placeholder-section').hide();
				jQuery('#pe-empty-section, #pe-registration-section').show();
				jQuery('#pe-syntax-section, #pe-min-section, #pe-max-section, #pe-max-size-section, #pe-extensions-section').hide();
				break;
			case 'radio':
				jQuery('#pe-name-section, #pe-label-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-placeholder-section').hide();
				jQuery('#pe-empty-section, #pe-registration-section').show();
				jQuery('#pe-syntax-section, #pe-min-section, #pe-max-section, #pe-max-size-section, #pe-extensions-section').hide();
				break;
			case 'picture':
				jQuery('#pe-name-section, #pe-label-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-placeholder-section').hide();
				jQuery('#pe-empty-section, #pe-max-size-section, #pe-extensions-section, #pe-registration-section').show();
				jQuery('#pe-syntax-section, #pe-min-section, #pe-max-section').hide();
				break;
			case 'password':
				jQuery('#pe-name-section, #pe-label-section, #pe-placeholder-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-empty-section, #pe-min-section, #pe-max-section, #pe-registration-section').show();
				jQuery('#pe-max-size-section, #pe-extensions-section, #pe-syntax-section').hide();
				break;
			case 'file':
				jQuery('#pe-name-section, #pe-label-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-placeholder-section').hide();
				jQuery('#pe-empty-section, #pe-max-size-section, #pe-extensions-section, #pe-registration-section').show();
				jQuery('#pe-syntax-section, #pe-min-section, #pe-max-section').hide();
				break;
			case 'dropdown':
				jQuery('#pe-name-section, #pe-label-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-placeholder-section').hide();
				jQuery('#pe-empty-section, #pe-registration-section').show();
				jQuery('#pe-syntax-section, #pe-min-section, #pe-max-section, #pe-max-size-section, #pe-extensions-section').hide();
				break;
			case 'hidden':
				jQuery('#pe-name-section, #pe-label-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-placeholder-section').hide();
				jQuery('#pe-empty-section, #pe-min-section, #pe-max-section, #pe-registration-section').show();
				jQuery('#pe-syntax-section, #pe-max-size-section, #pe-extensions-section').hide();
				break;
			case 'WYSIWYG':
				jQuery('#pe-name-section, #pe-label-section, #pe-placeholder-section, #pe-value-section, #pe-description-section').show();
				jQuery('#pe-empty-section, #pe-syntax-section, #pe-min-section, #pe-max-section, #pe-registration-section').show();
				jQuery('#pe-max-size-section, #pe-extensions-section').hide();
				break;
			default:
				//
		}
	  
	});

});